function mostrarError(mensaje){document.getElementById("error-message-container").innerHTML='<p class="error-message">'+mensaje+"</p>"}window.addEventListener("DOMContentLoaded",(event=>{const todo=document.querySelectorAll(".table tbody tr td");function celdaColor(td,res){if(res){td.classList.add("con-resultados")}else{td.classList.remove("con-resultados")}}function mostrarColor(){todo.forEach((td=>{const hora=td.innerText;const dia=td.parentNode.firstChild.innerText;fetch(`profesor/tabla_alumnos?&dia=${dia}&hora=${hora}&dni_prof=${dni_prof}`).then((response=>response.json())).then((data=>{celdaColor(td,data.length>0)})).catch((error=>{console.error("Hay un error en el fetch:",error)}))}))}mostrarColor();todo.forEach((td=>{td.addEventListener("click",(event=>{const hora=td.innerText;const dia=td.parentNode.firstChild.innerText;fetch(`profesor/tabla_alumnos?&dia=${dia}&hora=${hora}&dni_prof=${dni_prof}`).then((response=>response.json())).then((data=>{const ul=document.getElementById("resultado");ul.innerHTML="";for(let i=0;i<data.length;i++){const li=document.createElement("li");li.innerText=data[i].nombre+" "+data[i].apellidos+" - "+data[i].email;ul.appendChild(li)}})).catch((error=>console.error(error)))}))}))}));document.getElementById("btn").addEventListener("click",(function(){fetch("profesor/logout",{}).then((function(response){window.location.href="/App/app/index.php/inicio/principal"})).catch((function(error){console.log(error)}))}));setTimeout((function(){var errorMessage=document.getElementById("error-message");var successMessage=document.getElementById("success-message");if(errorMessage){errorMessage.style.display="none"}if(successMessage){errorMessage.style.display="none"}}),8e3);